     1→import client from "@sendgrid/client";
     2→import { NextResponse } from "next/server";
     3→import type z from "zod";
     4→import type { schemaNewsLetter } from "../../../components/Newsletter";
     5→import Logger from "../../../lib/logger";
     6→
     7→client.setApiKey(process.env.SENDGRID_API_KEY as string);
     8→
     9→export async function POST(request: Request) {
    10→  try {
    11→    const formValues = (await request.json()) as z.infer<typeof schemaNewsLetter>;
    12→
    13→    const data = {
    14→      list_ids: ["be1bdc02-c8d0-4bf6-b3a4-b53055256db9"],
    15→      contacts: [
    16→        {
    17→          email: formValues.email,
    18→        },
    19→      ],
    20→    };
    21→
    22→    const sendGridRequest = {
    23→      url: `/v3/marketing/contacts`,
    24→      method: "PUT" as const,
    25→      body: data,
    26→    };
    27→
    28→    const resp = await client.request(sendGridRequest);
    29→
    30→    process.env.NODE_ENV !== "production" &&
    31→      Logger.log({
    32→        level: "info",
    33→        message: JSON.stringify(resp),
    34→      });
    35→
    36→    return NextResponse.json({ error: "" }, { status: 200 });
    37→  } catch (error) {
    38→    Logger.log({
    39→      level: "error",
    40→      message: JSON.stringify(error),
    41→    });
    42→    return NextResponse.json({ error: "Mail Delivery failed" }, { status: 500 });
    43→  }
    44→}
    45→

