     1→import nodemailer from "nodemailer";
     2→import { NextResponse } from "next/server";
     3→import type z from "zod";
     4→import type { validationSchemaBuchung } from "../../../components/Buchung";
     5→import { renderBookingEmail } from "../../../lib/email/template-renderer";
     6→import Logger from "../../../lib/logger";
     7→
     8→// Create SMTP2GO transporter
     9→const transporter = nodemailer.createTransport({
    10→  host: "mail.smtp2go.com",
    11→  port: 587, // TLS port (recommended)
    12→  secure: false, // false for TLS
    13→  auth: {
    14→    user: process.env.SMTP2GO_USERNAME as string,
    15→    pass: process.env.SMTP2GO_PASSWORD as string,
    16→  },
    17→});
    18→
    19→export async function POST(request: Request) {
    20→  try {
    21→    const formValues = (await request.json()) as z.infer<typeof validationSchemaBuchung>;
    22→
    23→    // Render the email template with Handlebars
    24→    const htmlContent = renderBookingEmail({
    25→      name: formValues.name,
    26→      email: formValues.email,
    27→      message: formValues.message,
    28→      phone: formValues.phone,
    29→    });
    30→
    31→    // Send email to customer
    32→    const response = await transporter.sendMail({
    33→      from: '"Augenblick Chiemgau" <info@augenblick-chiemgau.com>',
    34→      to: formValues.email,
    35→      bcc: "info@augenblick-chiemgau.com",
    36→      subject: "Danke für deine Anfrage bei Augenblick Chiemgau",
    37→      html: htmlContent,
    38→    });
    39→
    40→    process.env.NODE_ENV !== "production" &&
    41→      Logger.log({
    42→        level: "info",
    43→        message: JSON.stringify(response),
    44→      });
    45→
    46→    return NextResponse.json({ error: "" }, { status: 200 });
    47→  } catch (error) {
    48→    Logger.log({
    49→      level: "error",
    50→      message: JSON.stringify(error),
    51→    });
    52→
    53→    return NextResponse.json({ error: "Mail Delivery failed" }, { status: 500 });
    54→  }
    55→}
